mapper.select:
select H.ID, H.APPLYUSER, H.LEVEL, H.PROJ, H.SEGMENT, H.WORKZOON, H.AREA, H.CONTRACTOR, H.WATCHFIRE, H.OPERATORS, H.STARTTIME, H.ENDTIME, H.DETIAL, H.CHECKPOINT, H.REMARKS, H.COMMITTIME, H.PROC_INST_ID_, H.STATUS, H.INTERVENTION from
H LEFT JOIN
AHI ON H.PROC_INST_ID_ = AHI.PROC_INST_ID_ where 1=1 and H.STATUS != '0'
and ( H.WORKZOON LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or H.CONTRACTOR LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or H.DETIAL LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or H.AREA LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or H.PROC_INST_ID_ = #{pd.keywords} )
and H.APPLYUSER = #{pd.APPLYUSER}
and H.PROJ=#{pd.PROJ}
and H.STATUS=#{pd.STATUS}
and AHI.USER_ID_=#{pd.ABOUTUSER}
GROUP BY H.PROC_INST_ID_ order by H.ID desc
