mapper.select:
select H.ID, H.APPLYUSER, H.SPRAYPART, H.WORKZOON AS CONTRACTOR, H.STARTTIME, H.ENDTIME, H.OIL1, H.OIL2, H.OIL3, H.OPERATE, H.DRAUGHT, H.FGUARDIAN, H.OPERATOR, H.PRINCIPAL, H.AGUARDIAN, H.SAFETY, H.HAVESPACE, H.STATUS, H.PROC_INST_ID_, H.CHECKPOINT, H.COMMITTIME, H.PAREA AS AREA, H.PROJ, H.INTERVENTION from
H LEFT JOIN
AHI ON H.PROC_INST_ID_ = AHI.PROC_INST_ID_ where 1=1 and H.STATUS != '0'
and ( H.WORKZOON LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or H.OPERATOR LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or H.FGUARDIAN LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or H.PAREA LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or H.PROC_INST_ID_ = #{pd.keywords} )
and H.APPLYUSER = #{pd.APPLYUSER}
and H.STATUS=#{pd.STATUS}
and AHI.USER_ID_=#{pd.ABOUTUSER}
GROUP BY H.PROC_INST_ID_ order by H.ID desc
