mapper.select:
select H.ID, H.APPLYUSER, H.WORK_ZOON, H.ERECTION_ZOON AS CONTRACTOR, H.INSPECTION_TIME, H.USEFOR, H.ERECTION_LOCATION AS AREA, H.CHECK_POINT, H.MARK, H.COMMIT_TIME, H.PROJ_NO AS PROJ, H.SUBSECTION, H.PROC_INST_ID_, H.STATUS, H.WORKER, H.INTERVENTION from
H LEFT JOIN
AHI ON H.PROC_INST_ID_ = AHI.PROC_INST_ID_ where 1=1
and ( H.WORKZOON LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or H.ERECTION_ZOON LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or H.ERECTION_LOCATION LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or H.ERECTION_LOCATION LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or H.PROC_INST_ID_ = #{pd.keywords} )
and H.APPLYUSER = #{pd.APPLYUSER}
and H.PROJ_NO=#{pd.PROJ_NO}
and H.STATUS=#{pd.STATUS}
and AHI.USER_ID_=#{pd.ABOUTUSER}
GROUP BY H.PROC_INST_ID_ order by H.ID desc
