mapper.select:
select od.NAME,
from
H left join oa_department od on H.department = od.DEPARTMENT_ID where process_id = #{process_id}

select od.NAME, H.process_id, H.door, H.user_id as aaa, H.department, H.car_num, H.state, H.ex_company, H.a_pile_of_shit, H.committime from
H left join oa_department od on H.department = od.DEPARTMENT_ID where H.state !='0'
and ( H.ex_company LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or H.process_id LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and H.state=#{pd.STATUS}
and H.user_id=#{pd.USERNAME}
order by H.committime desc
