mapper.select:
select H.ID, H.APPLYUSER, H.PROJECTNAME AS PROJ, H.PROJECTLEADER, H.CONSTRUCTION AS CONTRACTOR, H.JOBLOCATION AS AREA, H.ENTERTIME, H.TENTERTIME, H.CHECKPOINT, H.PROBLEM, H.COMMITTIME, H.PROC_INST_ID_, H.STATUS, H.FILE_PATH, H.INTERVENTION from
H LEFT JOIN
AHI ON H.PROC_INST_ID_ = AHI.PROC_INST_ID_ where 1=1
and ( H.CONSTRUCTION LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or H.PROJECTLEADER LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or H.JOBLOCATION LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or H.JOBLOCATION LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or H.PROC_INST_ID_ = #{pd.keywords} )
and H.APPLYUSER = #{pd.APPLYUSER}
and H.PROJECTNAME=#{pd.PROJECTNAME}
and H.STATUS=#{pd.STATUS}
and AHI.USER_ID_=#{pd.ABOUTUSER}
GROUP BY H.PROC_INST_ID_ order by H.ID desc
